/*! \package dominio.comun
 */

var especificacion_abstracta =
    require('gueco/dominio/comun/especificacion_abstracta')

especificacion_o.prototype = new especificacion_abstracta
especificacion_o.prototype.constructor = especificacion_o

especificacion_o.prototype.validar = function(t) {
    return this.spec1.validar(t) || this.spec2.validar(t)
}

function especificacion_o (spec1, spec2) {
    this.spec1 = spec1
    this.spec2 = spec2
}

module.exports = especificacion_o
