/*! \package dominio.comun
 */

var especificacion_abstracta = require('gueco/dominio/comun/especificacion_abstracta')

especificacion_objeto.prototype = new especificacion_abstracta
especificacion_objeto.prototype.constructor = especificacion_objeto

especificacion_objeto.prototype.validar = function(t) {
    for (var a in t)
        if (this.objeto[a] != t[a]) return false
    return true
}

function especificacion_objeto (objeto) {
    this.objeto = objeto
}

module.exports = especificacion_objeto
