/*! \package dominio.comun
 */

var especificacion_abstracta =
    require('gueco/dominio/comun/especificacion_abstracta')

especificacion_y.prototype = new especificacion_abstracta
especificacion_y.prototype.constructor = especificacion_y

especificacion_y.prototype.validar = function(t) {
    return this.espec1.validar(t) && this.espec2.validar(t)
}

function especificacion_y (spec1, spec2) {
    this.espec1 = espec1
    this.espec2 = espec2
}

module.exports = especificacion_y
