especificador.prototype.especificar = function() {
    throw new Error('no implementado')
}

especificador.prototype.fijar_especificante = function(e) {
    this.especifica = this.especificar(this.especificante = e)
}

function especificador (especificante) {
    this.especificante = especificante
    this.especifica = this.especificante ?
        this.especificar(this.especificante) : null
}

module.exports = especificador
