var especificacion_abstracta = require('gueco/dominio/comun/especificacion_abstracta')

especificacion_usuarios.prototype = new especificacion_abstracta
especificacion_usuarios.prototype.constructor = especificacion_usuarios

especificacion_usuarios.prototype.validar = function(t) {
    return t.id in usuarios
}

especificacion_usuarios.prototype.a_consulta = function(adr) {
    return { _id: { $in: this.usuarios.map(function(i){return adr(i)}) } }
}

function especificacion_usuarios (usuarios) {
    this.usuarios = usuarios
}

module.exports = especificacion_usuarios
