/*! \package gueco.infraestructura.persistencia.mongo
 */

var especificador = require('gueco/dominio/comun/especificador')

var aId =
    require('gueco/infraestructura/persistencia/mongoskin/repositorio').aId

especificador_escenarios.prototype.especificar = function() {
    return {
        _id: { $in :this.especificante.map(function(i){return aId(i)}) }
    }
}

especificador_escenarios.prototype.fijar_especificante = function(e) {
    this.especifica = this.especificar(this.especificante = e)
}

function especificador_escenarios (escenarios) {
    especificador.call(this, escenarios)
}

module.exports = especificador_escenarios
