controlador_recurso.prototype.listar = function(peticion, respuesta) {

    var repo = this.fachada_servicio_recurso.repositorio;

    this.fachada_servicio_recurso.listar(function(error, recursos) {
        respuesta.send(error ? 500 : recursos)
    })
}

controlador_recurso.prototype.obtener = function(peticion, respuesta) {
    if(peticion.usuario.escenarios.indexOf(peticion.params.id) != -1)
        this.fachada_servicio_recurso.obtener(peticion.params.id,
                                              function(error, recurso) {
            respuesta(error ? 500 : recurso)
        })
    else respuesta.send(401)
}

function controlador_recurso () {}

module.exports = controlador_recurso
