/*! \package gueco.interfaz.controlador
 */

controlador_usuario.prototype.escenarios = function(peticion, respuesta) {
    this.fachada_servicio_usuario.escenarios(peticion.usuario,
                                             function(error, escenarios) {
        respuesta.send(error ? 500 : escenarios)
    })
}

controlador_usuario.prototype.escenario = function(peticion, respuesta) {
    if(peticion.usuario.escenarios.indexOf(peticion.params.id) != -1)
        this.fachada_servicio_usuario.escenario(peticion.params.id,
                                               function(error, escenario) {
            respuesta.send(error ? 500 : escenario)
        })
    else respuesta.send(404)
}

controlador_usuario.prototype.almacenar_escenarios =
    function(peticion, respuesta) {

    this.fachada_servicio_usuario.almacenar_escenarios(peticion.usuario,
                                                       peticion.cuerpo,
        function(error, id) {
        respuesta.send(id ? id : 200)
    })
}

controlador_usuario.prototype.borrar_escenario =
    function(peticion, respuesta) {

    this.fachada_servicio_usuario.borrar_escenario(peticion.params.id,
                                                   peticion.cuerpo,
                                                   peticion.usuario,
        function(error, id) {
        respuesta.send(id ? id : 200)
    })
}


function controlador_usuario () {}

module.exports = controlador_usuario
