/*! \package gueco.interfaz.recurso.fachada.interno
 */

servicio_recurso.prototype.listar = function(retrollamada) {
    this.repositorio.todos(function(error, recursos) {
        var otd = null
        if (!error) {
            otd = {}
            for (var i in recursos) {
                var recurso = recursos[i]
                otd[recurso.id] = recurso
            }
        }
        retrollamada(error, otd)
    })
}

servicio_recurso.prototype.obtener = function(id, retrollamada) {
    this.repositorio.uno({id: id}, retrollamada)
}

function servicio_recurso () {}

module.exports = servicio_recurso
